&lt;figure class=&#34;highlight matlab&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;t=&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;:&lt;span class=&#34;number&#34;&gt;0.0001&lt;/span&gt;:&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;*&lt;span class=&#34;built_in&#34;&gt;pi&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;y=&lt;span class=&#34;built_in&#34;&gt;sin&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;0.5&lt;/span&gt;*&lt;span class=&#34;built_in&#34;&gt;pi&lt;/span&gt;*t)+&lt;span class=&#34;number&#34;&gt;0.1&lt;/span&gt;*&lt;span class=&#34;built_in&#34;&gt;randn&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;,&lt;span class=&#34;built_in&#34;&gt;length&lt;/span&gt;(t));&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;y1=&lt;span class=&#34;built_in&#34;&gt;zeros&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;,&lt;span class=&#34;built_in&#34;&gt;length&lt;/span&gt;(y)&lt;span class=&#34;number&#34;&gt;-2&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;i&lt;/span&gt;=&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;:&lt;span class=&#34;built_in&#34;&gt;length&lt;/span&gt;(y)&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;y1(&lt;span class=&#34;built_i